import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Moment from 'react-moment';
import TextField from '@material-ui/core/TextField';
import Snackbar from '@material-ui/core/Snackbar';

import { userActions } from '../_actions';
import { userService } from '../_services';

class TodoPage extends React.Component {
  constructor(props, context) {
    super(props, context);

    this.state = {
      todos: [],
      limit: 15,
      offset: 0,
      title: '',
      description: '',
      user_id: '',
      status: 'todo',
      project_id: '',
      table: false,
      labelWidth: 0
    };
  }

  componentDidMount() {
    this.props.dispatch(userActions.getAll());
    userService.getProjects().then(projects => {
      if (projects) {
        this.setState({
          projects: projects
        })
      }
    });
  }

  handleChange = (name, event) => {
    this.setState({ [name]: event.target.value });
  };

  onTodoSubmit = () => {
    const { cookies } = this.props
    let jwt = cookies.get(this.state.cookieName)
    let payload = { 
                    title: this.state.title, 
                    description: this.state.description,
                    user_id: this.state.user_id,
                    status: this.state.status,
                    project_id: this.state.project_id
                  }
    userService.createTodo(payload, jwt).then(projects => {
      this.setState({ 
        table: true,
        snackBar: true
      });
    })
  }

  render() {
    const { user, users } = this.props;
    return (
      this.state.table ?
        <Paper className='project-table'>
        <Table className='project-table-width'>
          <TableHead>
            <TableRow>
              <TableCell align="right">Title</TableCell>
              <TableCell align="right">Description</TableCell>
              <TableCell align="right">Status</TableCell>
              <TableCell align="right">User</TableCell>
              <TableCell align="right">Project</TableCell>
              <TableCell align="right">Created At</TableCell>
              <TableCell align="right">Updated At</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.todos.map(row => (
              <TableRow key={row.id}>
                <TableCell align="right">{row.title}</TableCell>
                <TableCell align="right">{row.description}</TableCell>
                <TableCell align="right">{row.status}</TableCell>
                <TableCell align="right">{row.user_id}</TableCell>
                <TableCell align="right">{row.project_id}</TableCell>
                <TableCell align="right"><Moment format="YYYY/MM/DD">{row.created_at}</Moment></TableCell>
                <TableCell align="right"><Moment format="YYYY/MM/DD">{row.updated_at}</Moment></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </Paper>
      : 
      <div>
        <form className='project-create-form' noValidate autoComplete="off">
        <TextField
          id="outlined-name"
          label="Title"
          className='project-text-field'
          value={this.state.name}
          onChange={this.handleChange.bind(this, 'title')}
          margin="normal"
          variant="outlined"
        />
        <TextField
          id="outlined-multiline-static"
          label="Description"
          multiline
          rows="10"
          onChange={this.handleChange.bind(this, 'description')}
          value={this.state.description}
          defaultValue="Default Value"
          className='project-text-field'
          margin="normal"
          variant="outlined"
        />
        <FormControl variant="outlined" className='todo-select-field'>
          <InputLabel
            ref={ref => {
              this.InputLabelRef = ref;
            }}
            htmlFor="outlined-age-simple"
          >
            Projects
          </InputLabel>
          <Select
            value={this.state.age}
            onChange={this.handleChange.bind(this, 'project_id')}
            input={
              <OutlinedInput
                labelWidth={this.state.labelWidth}
                name="age"
                id="outlined-age-simple"
              />
            }
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <FormControl variant="outlined" className='todo-select-field'>
          <InputLabel
            ref={ref => {
              this.InputLabelRef = ref;
            }}
            htmlFor="outlined-age-simple"
          >
            Developers
          </InputLabel>
          <Select
            value={this.state.age}
            onChange={this.handleChange.bind(this, 'user_id')}
            input={
              <OutlinedInput
                labelWidth={this.state.labelWidth}
                name="age"
                id="outlined-age-simple"
              />
            }
          >
            <MenuItem value={10}>Ten</MenuItem>
            <MenuItem value={20}>Twenty</MenuItem>
            <MenuItem value={30}>Thirty</MenuItem>
          </Select>
        </FormControl>
        <Button variant="contained" color="primary" styles={{ margin: '10px'}} onClick={this.onTodoSubmit.bind(this)}>
          Submit
        </Button>
      </form>
      </div>
    )
  }
}

function mapStateToProps(state) {
  const { users, authentication } = state;
  const { user } = authentication;
  return {
    user,
    users
  };
}

const connectedTodoPage = connect(mapStateToProps)(TodoPage);
export { connectedTodoPage as TodoPage };
