import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import InboxIcon from '@material-ui/icons/MoveToInbox';
import MailIcon from '@material-ui/icons/Mail';


import { userActions } from '../_actions';

class HomePage extends React.Component {
    state = {
      left: false
    };

    componentDidMount() {
        this.props.dispatch(userActions.getAll());
    }

    onProjectClick = () => {

    }

    toggleDrawer = (side, open) => () => {
      this.setState({
        [side]: open,
      });
    };

    render() {
        const { user, users } = this.props;
        const sideList = (
          <div className='side-bar'>
            <List>
              {['Projects', 'Todos'].map((text, index) => (
                <ListItem button key={text} onClick={this.onProjectClick()}>
                  <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List>
            <Divider />
            {/* <List>
              {['All mail', 'Trash', 'Spam'].map((text, index) => (
                <ListItem button key={text}>
                  <ListItemIcon>{index % 2 === 0 ? <InboxIcon /> : <MailIcon />}</ListItemIcon>
                  <ListItemText primary={text} />
                </ListItem>
              ))}
            </List> */}
          </div>
        );

        return (
            <div>
              <div className='app-header'>
                <SwipeableDrawer
                  open={this.state.left}
                  onClose={this.toggleDrawer('left', false)}
                  onOpen={this.toggleDrawer('left', true)}
                >
                  <div  tabIndex={0}
                        role="button"
                        onClick={this.toggleDrawer('left', false)}
                        onKeyDown={this.toggleDrawer('left', false)} >
                        {sideList}
                  </div>
                </SwipeableDrawer>
              <AppBar position="static">
                <Toolbar>
                  <IconButton className='app-header-menuButton' onClick={this.toggleDrawer('left', true)} color="inherit" aria-label="Menu">
                    <MenuIcon />
                  </IconButton>
                  <div style={{flex: 'auto'}}>
                    <Typography variant="h6" color="inherit" className='app-header'>
                      Dashboard
                    </Typography>
                  </div>
                  <div>
                    <Typography variant="h6" color="white" className='app-header'>
                      <Link className='' to="/login">Log Out</Link>
                    </Typography>
                  </div>
                </Toolbar>
              </AppBar>
            </div>
          </div>
        );
    }
}

function mapStateToProps(state) {
    const { users, authentication } = state;
    const { user } = authentication;
    return {
        user,
        users
    };
}

const connectedHomePage = connect(mapStateToProps)(HomePage);
export { connectedHomePage as HomePage };
